import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;

  div{
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;

    h1{
      font-weight: bold;
      font-family: 'Roboto';
      font-size: 100px;
    }

    form{

      div{

        input{
          height: 40px !important;
          margin: 10px;
        }

      }

    }

    .submitButton{
      width: 150px !important;
    }

  }

`;
