export default styleInject;
import React, { Component } from 'react'
import PropTypes from 'prop-types'

export class NeuProgressBar extends Component {
  static propTypes = {
    width: PropTypes.string
  }

  render() {
    const {
      progress
    } = this.props

    return (
    <div style={{    
      position: 'relative',
      width: '100%',
      height: '16px',
      borderRadius: '8px',
      backgroundColor: '#e0e5ec',
      boxShadow: 'rgba(0, 0, 0, 0.05) 9px 9px 9px, rgba(255, 255, 255, 0.5) -9px -9px 9px, rgba(0, 0, 0, 0.05) 4px 4px 4px inset, rgba(255, 255, 255, 0.5) -4px -4px 4px inset',
      marginBottom: '56px'}} >
    <div style={{
      width: progress||'50%',
      height: '16px',
      left: '2px',
      top: '2px',
      borderRadius: '6px',
      backgroundColor: 'rgb(185, 194, 206)',
  }}></div>
  </div>
    )
  }
}
import React, { Component } from 'react'
import PropTypes from 'prop-types'

export class NeuReaction extends Component {
  static propTypes = {
    size: PropTypes.string
  }
  constructor(props){
    super(props);
    this.state = {
      clicked: false
    }
  }

  handleClick = () => {
    this.setState({
      clicked: !this.state.clicked
    })
  }

  render() {
    const {
      size,
      children
    } = this.props

    return (
      <button onClick={this.handleClick} style={ this.state.clicked ? {
      borderRadius: '50%',
      width: size||'70px',
      height: size||'70px',
      border: '0px',
      cursor: 'pointer',
      outline:'0px',
      backgroundColor: '#E0E5EC',
      boxShadow: '9px 9px 16px rgba(0, 0, 0, 0), -9px -9px 16px rgba(247, 251, 255, 0), 9px 9px 16px rgba(0, 0, 0, 0.1) inset, -9px -9px 16px rgba(247, 251, 255, 0.7) inset'
      } : {borderRadius: '100px',textAlign: 'center',
      width: size||'70px',
      height: size||'70px',
      border: '0px',
      cursor: 'pointer',
      outline:'0px',
      backgroundColor: '#E0E5EC',
      boxShadow: '9px 9px 16px rgb(163,177,198,0.6), -9px -9px 16px rgba(255,255,255, 0.5)'}}>
      {children}
      </button>
    )
  }
}
import React, { Component } from 'react'
import PropTypes from 'prop-types'

export class NeuButton extends Component {
  static propTypes = {
    text: PropTypes.string,
    hovered: PropTypes.bool,
    clicked: PropTypes.bool,
    mouseOver: PropTypes.func,
    mouseOut: PropTypes.func,
    onClick: PropTypes.func,
  }

  render() {
    const {
      text,
      hovered,
      clicked,
      mouseOver,
      mouseOut,
      onClick
    } = this.props

    return (
    <div style={{
      display:'block',
      borderRadius: '4px',
      paddingTop: '20px',
      outline: '0',
      fontWeight: '600',
      color: hovered ? '#e0e5ec' : '#b9c2ce',
      paddingBottom: '20px',
      textAlign: 'center',
      border: '0',
      cursor: 'pointer',
      width: '100%',
      backgroundColor: hovered ? '#b9c2ce' : '#E0E5EC',
      boxShadow: clicked ? '9px 9px 16px rgba(0, 0, 0, 0), -9px -9px 16px rgba(247, 251, 255, 0), 9px 9px 16px rgba(0, 0, 0, 0.1) inset, -9px -9px 16px rgba(247, 251, 255, 0.7) inset' : '9px 9px 16px rgb(163,177,198,0.6), -9px -9px 16px rgba(255,255,255, 0.5)',
    }} onMouseOver={mouseOver} onMouseOut={mouseOut} onClick={onClick}>{text}</div>
    )
  }
}
import React, { Component } from 'react'
import PropTypes from 'prop-types'

export class NeuInput extends Component {
  static propTypes = {
    width: PropTypes.string,
    placeholder: PropTypes.string,
    value: PropTypes.string,
    onChange: PropTypes.func,
  }

  render() {
    const {
      width, placeholder, value, onChange
    } = this.props

    return (
  <div>
  <input style={{ borderRadius: '10px',
    outline:'0',
    width: width||'auto',
    height: '20px',
    padding: '5px',
    fontWeight:'600',
    color: '#a1a9b5',
    paddingLeft: '8px',
    border: '5px solid #e8eef7',
    backgroundColor: 'rgb(224, 229, 236,0.6)',
    boxShadow: 'inset 3px 3px 3px rgba(163,177,198, 0.7),inset -3px -3px 4px rgba(255, 255, 255, 0.5), 9px 9px 16px rgb(163,177,198,0.6)'}} placeholder={placeholder||""} value={value} onChange={onChange} />
  </div>
    )
  }
}
import React, { Component } from 'react'
import PropTypes from 'prop-types'

export class NeuCard extends Component {
  static propTypes = {
    width: PropTypes.string,
    height: PropTypes.string,
  }

  render() {
    const {
      children,
      width,
      height
    } = this.props

    return (
      <div style={{
        borderRadius: '4px',
        padding: '20px',
        textAlign: 'center',
        height: height||'15px',
        width: width||'100%',
        backgroundColor: '#E0E5EC',
        boxShadow: '9px 9px 16px rgb(163,177,198,0.6), -9px -9px 16px rgba(255,255,255, 0.5)'
    }}>{children}
      </div>
    )
  }
}
import React, { Component } from 'react'
import PropTypes from 'prop-types'

export class NeuHeading extends Component {
  static propTypes = {
    fontSize: PropTypes.string,
    fontWeight: PropTypes.string,
    text: PropTypes.string,
  }

  render() {
    const {
      text, fontSize, fontWeight
    } = this.props

    return (
    <div style={{
      borderRadius: '7px solid white',
      width: '100%',
      display: 'inline-block',
      marginBottom:fontSize,
      wordWrap:'break-word',
      fontSize: fontSize||'100px',
      fontWeight: fontWeight||'900',
      color: '#E0E5EC',
      textShadow: '3px 3px 9px rgb(163,177,198,0.6)'
