import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Container, Profile } from './styles';

import defaultProfileImage from '../../assets/default.png';

export default function Sidebar() {
  const [firstSelected, setFirstSelected] = useState(true);
  const [secondSelected, setSecondSelected] = useState(false);
  const [thirdSelected, setThirdSelected] = useState(false);
  const [fourthSelected, setFourthSelected] = useState(false);

  function clearSelected() {
    setFirstSelected(false);
    setSecondSelected(false);
    setThirdSelected(false);
    setFourthSelected(false);
  }

  function firstClicked() {
    clearSelected();
    setFirstSelected(true);
    event.target.style = { color: "black" }
  }

  function secondClicked() {
    clearSelected();
    setSecondSelected(true);
    event.target.style = { color: "black" }
  }

  function thirdClicked() {
    clearSelected();
    setThirdSelected(true);
    event.target.style = { color: "black" }
  }

  function fourthClicked() {
    clearSelected();
    setFourthSelected(true);
    event.target.style = { color: "black" }
  }

  return (
    <Container>
      <Profile>
        <img src={defaultProfileImage} alt="Profile" />
      </Profile>
      <ul>
        <Link onClick={e => { firstClicked(e); }}><li>VENDAS</li></Link>
        <Link onClick={e => { secondClicked(e) }}><li>CLIENTES</li></Link>
        <Link onClick={e => { thirdClicked(e) }}><li>CARTEIRA</li></Link>
        <Link onClick={e => { fourthClicked(e) }}><li>CONTATO</li></Link>
      </ul>
    </Container>
  );
}
