import styled from 'styled-components';

export const Container = styled.div`
  min-width: 100%;
  height: 100px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-weight: 400;

  .menu{
    min-width: 90%;
    display: flex;
    justify-content: space-between;
    align-items: center;

    ul{
      list-style: none;

      li{

        a{
          text-decoration: none;
          color: #555;
        }

        float: left;
        cursor: pointer;
        padding-left: 10px;
        margin-right: 10px;

        .selected{
          background-image: linear-gradient(to right, #36D1DC, #5B86E5);
          width: 10px;
          min-height: 2px;
        }

      }
    }

    .options{
      display: flex;
      align-items: center;
      color: #FFF;
    }

  }
`;
